import { Component, OnInit } from '@angular/core';
import {
  FormBuilder,
  FormGroup,
  Validators
 } from '@angular/forms';

@Component({
  selector: 'app-form-builder',
  templateUrl: './form-builder.component.html',
  styleUrls: ['./form-builder.component.css']
})
export class FormBuilderComponent implements OnInit{
  myForm!: FormGroup;

  constructor(private fb: FormBuilder) {
    this.myForm =this.fb.group({
      sku : ['', Validators.required]
    });
  }

  ngOnInit(){}

  onSubmit(value: string): void{
    if (this.myForm.valid){
      console.log('form submitted successfully! ', this.myForm.value);

    }else{
      console.log('form is invalid');

    }

  }

}

<div class="user-I">
    <h2>Demo Angular Forms</h2>

    <form *ngIf="myForm" [formGroup]="myForm" (ngSubmit)="onSubmit(myForm.value)" class="formContainer">
        <div class="field">
            <label for="skuInput">sku: </label>
            <input type="text" 
                 id="skuInput" 
                placeholder="SKU" 
                formControlName ="sku">

            <div *ngIf="myForm.get('sku')!.hasError('required') && myForm.get('sku')!.touched">
                sku is required.
            </div>
        </div>

        <button type="submit" class="ui-button">Submit</button>

    </form>
</div>
